# -*- coding: UTF-8 -*-

"""PFE Component Tests - ProximityZone.

* TC-43840 - ProximityZone POST:

  Verify Correct Error Message Displayed On Providing Max Length Greater Than 1000 Characters In Notes Field.


Equivalent test JSON payload:

{'configAdminCanEdit': False,
 'configurations': [],
 'id': 'qa13',
 'name': 'metricmax1le32ngh22',
 'proximityDetails': [{'cidr': '0.0.0.0/0',
                       'metric': 3,
                       'notes': ''}],
 'visibleInAllConfigurations': True}

"""

import pytest

from qe_common import *

logger = init_logger()


@pytest.mark.components
@pytest.allure.story('ProximityZone')
@pytest.allure.feature('POST')
class Test_PFE_Components(object):
    """PFE ProximityZone test cases."""

    @pytest.allure.link('https://jira.qumu.com/browse/TC-43840')
    @pytest.mark.ProximityZone
    @pytest.mark.POST
    def test_TC_43840_POST_ProximityZone_Providing_Max_Length_Greater_Than_1000_Characters_In_Notes_Field(self, context):
        """TC-43840 - Verify Correct Error Message Displayed On Providing Max Length Greater Than 1000 Characters In Notes Field"""
        # Define a test step
        with pytest.allure.step('Verify Correct Error Message Displayed On Providing Max Length Greater Than 1000 Characters In Notes Field.'):
            proximityDetails = context.sc.ProximityDetails(
                cidr='0.0.0.0/0',
                metric=3,
                notes='')
            proximityZone = context.sc.ProximityZoneDetails(
                visibleInAllConfigurations=True,
                configAdminCanEdit=False,
                configurations=[],
                id='qa13',
                name='metricmax1le32ngh22',
                proximityDetails=[proximityDetails])
            # POST the ProximityZone, and check we got the error we expect
            try:
                client, response = check(
                    context.cl.Proximity_Zones.createEntity(body=proximityZone),
                    quiet=True, returnResponse=True)
            except HTTPBadRequest as e:         # 400 error
                get_error_message(e) | should.contain('length must be between 0 and 1000')
            else:
                raise Exception(
                    "Expected error message, got {} status code instead.".format(
                        response.status_code))
